void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 0.2;
    float fastT = iTime * 0.7;

    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    uv *= dot(uv, vec2(0.5, -0.9)) * 30.0;
    uv.x += t;
    uv.y += sin(uv.x) + 
            cos(uv.x * 0.5 - fastT) + 
            (sin(uv.x * 2.0 + fastT) * 0.4);
    float f = mod(floor(uv.y), 3.0) * 0.3333;
    float m1 = step(f, 0.333);
    float m2 = step(f, 0.334);
    vec3 result = mix(vec3(m2), vec3(1.0, 0.0, 0.22) * m1, m1);
    fragColor = vec4(result, 1.0);
}
