#define pi acos(-1.)

vec2 RotateUV(vec2 uv, float rotation) {
    return vec2(sin(rotation) * uv.y + cos(rotation) * uv.x, sin(rotation) * uv.x - cos(rotation) * uv.y); }

float Line(vec2 uv, vec2 a, vec2 b) {
	vec2 uva = uv - a, ba = b - a;
    float t = clamp(dot(uva, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a + ba * t;
    float d = length(c - uv);
	return min(1.0, 0.001/d); }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;





    vec3 col = vec3(0.12, 0.12, 0.12);

    col = mix(col, vec3(0.1, 0.1, 0.1) + uv.y + uv.x, smoothstep(0.0, 1., uv.y ));



    for (int i = 0; i<=60; i++) {
        float rotation = pi * float(i)/float(60) * 2.;
        vec2 RotP2 = uv;
        RotP2 = RotateUV(uv, rotation);
        float dt = smoothstep(0.5, 1., Line(RotP2, vec2(0.0,0.1), vec2(0.0, 0.15)));

        col = mix(col, vec3(((float(60-i)*((cos(iTime*1.)*0.5+0.5)))/30.)*0.5+0.5, (float(30.+30.*sin(float(i)))*((cos(iTime*3.)*0.5+0.5))/30.)*0.5+0.5, ((float(i)*((sin(iTime*5.)*0.5+0.5)))/30.)*0.5+0.5), dt); }


