void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 scale = vec2(3.7, .4);

    float speed = 2.1;
    float b = .04;
    vec4 noise = texture(iChannel1, uv*scale) * pow(uv.y +.3, 3.);

    vec2 uv2 = vec2(uv.x + noise.r*.2 + cos(iTime*speed+uv.x*speed)*b*noise.r, uv.y + (sin(iTime*speed+uv.y*speed)+.3)*b*noise.g);

    vec4 c = texture(iChannel0, uv2 *scale - vec2(0., .2));

    vec4 base = vec4(c.x,c.y,c.z,c.x) * smoothstep(.0, .9, c.x) + texture(iChannel1,uv) * .2;



    fragColor = vec4(base.x, smoothstep(1.,0.,abs(.6-uv.y)*1.4) * .2+ base.y , base.z, base.w) ;
}
