void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t1 = sin(iTime*0.25);
    float t2 = cos(iTime*0.3);
    float t3 = sin(iTime*0.1);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float d = 0.01 / abs(length(uv)-0.5);
    float v = (1.2 * abs(length(uv)+0.4) - abs(length(uv)+0.45));
    d += v;
    vec3 color = vec3(d*t1,d*t2,d*t3);
    fragColor = vec4(color,1.0);
}
