float pointInRectStroke(vec4 rect, float b /*borderWidth*/, vec2 p){

    if(p.x >= rect.x && p.y >= rect.y && p.x <= rect.z && p.y <= rect.w ){
        if(p.x > rect.x+b && p.y > rect.y+b && p.x < rect.z-b && p.y < rect.w-b){
            return 1.;
        }
        return 0.;
    }else{
        return 1.;
    }
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 bgColor = vec4(1.0);
    vec4 red = vec4(1., 0., 0., 1.);

    vec4 rect1 = vec4(100, 100, 400, 300);
    float d = pointInRectStroke(rect1, 4., fragCoord);
    fragColor = mix(red, bgColor, d);
}
