void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy; // normalize XY 
    vec3 viewDir = vec3(uv.x, uv.y - 0.5, 0.5);  
    vec2 planarUV = vec2(viewDir.x, viewDir.z / abs(viewDir.y));

    vec3 txtre = texture(iChannel0, planarUV).rgb; // puts texture into dropped XY

    fragColor = vec4(txtre, 1.0);
}






