void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    float timeSkew = sin(iTime * 8.0) * 0.5;
    float timeSkew2 = sin(iTime * 2.0) * 0.5 + 0.5;
    uv.x += uv.y * timeSkew;
    uv.y += uv.x * timeSkew;
    uv.x += uv.y * timeSkew;
    uv.y -= uv.x * timeSkew;
    uv.x -= uv.y * timeSkew;
    uv.y -= uv.x * timeSkew;
    uv.x -= uv.y * timeSkew * timeSkew2;
    uv.y -= uv.x * timeSkew * timeSkew2;
    uv.x -= uv.y * timeSkew * timeSkew2;
    uv.y += uv.x * timeSkew * timeSkew2;
    uv.x += uv.y * timeSkew * timeSkew2;
    uv.y += uv.x * timeSkew * timeSkew2;
    uv += 0.5;
    fragColor = texture(iChannel0, uv);
}
