void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 mouse = vec2(0.5,0.2);
if (iMouse.z>0.0){
    mouse = iMouse.xy/iResolution.xy;}
float blur = ceil(10.0*mouse.y);
vec3 col = vec3(0.0,0.0,0.0);
    //vec3 col = texture(iChannel0,vec2(fragCoord/iResolution.xy)).rgb;
for (float a=-blur;a<blur+1.0;++a){
    float aRad = sqrt(pow(blur,2.0)-pow(a,2.0));
    for (float b=-aRad; b<aRad+1.0; ++b){
        float area = ceil(3.14159*pow(blur,2.0));
    col += texture(iChannel0,vec2(vec2((iResolution.x-fragCoord.x)+a,fragCoord.y+b)/iResolution.xy)).rgb/area;
    }}

    fragColor = vec4(step(mouse.x,col),1.0);
}
