#define MAX_STEPS 250
#define MAX_DISTANCE 250.
#define SURFACE_DISTANCE .025

float GetDist(vec3 point) {
    vec3 spherePosition = vec3(
        4. * cos(iTime * 2.),
        1.4,
        9. + sin(iTime * 2.) * 5.
    );
    float radius = abs(cos(iTime * 2.)) * .4 + .4;
    vec4 sphere = vec4(spherePosition, radius);
    float sphereDist = length(point - sphere.xyz) - sphere.w;
    float planeDist = point.y;    // for a ground plane 

    float dist = tan(abs(min(sphereDist, planeDist)));
    return dist;
}

float RayMarch(vec3 rayOrigin, vec3 rayDirection) {
    float distanceOrigin = 0.;

    for(int i = 0; i<MAX_STEPS; i++) {
        vec3 point = rayOrigin + rayDirection*distanceOrigin;
        float distanceScene = GetDist(point);
        distanceOrigin += distanceScene;
        if(distanceOrigin > MAX_DISTANCE || distanceScene < SURFACE_DISTANCE) break;    
    }

    return distanceOrigin;
}

vec3 GetNormal(vec3 point) {
    float dist = GetDist(point);
    vec2 e = vec2(.01, 0);
    vec3 n = dist - vec3(
        GetDist(point - e.xyy),
        GetDist(point - e.yxy),
        GetDist(point - e.yyx)
    );

    return normalize(n);
}

float GetLight(vec3 point) {
    vec3 lightPos = vec3(0, 5, 6);
    //lightPos.xz =+ vec2(sin(iTime), tan(iTime))*2.;
    vec3 light = normalize(lightPos - point);



    vec3 normal = GetNormal(point);
    float diffuse = dot(normal, light);
    diffuse = clamp(diffuse, 0., 1.);

    float rayDistance = RayMarch(point + normal*SURFACE_DISTANCE*2., light);  // need to move out of the surface or ray march will exit early
    float actualDistance = length(lightPos - point);
    bool shadow = rayDistance < actualDistance;
    if(shadow) diffuse *= (diffuse + .05)*.5;

    return diffuse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 0 in the center
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    vec3 changingColor = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col = vec3(0);

    //camera
    vec3 rayOrigin = vec3(0, 1, 0); 
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    float distance = RayMarch(rayOrigin, rayDirection);
    vec3 point = rayOrigin + rayDirection * distance;

    float diffuse = GetLight(point);

    col = vec3(diffuse); 

    col *= changingColor;

    // Output to screen
    fragColor = vec4(col,1.0);
}
