const float PI = acos(-1.0);
const float TAU = PI*2.0;

// https://iquilezles.org/articles/palettes/
// cosine based palette, 4 vec3 params
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.283185*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= vec2(iResolution.x/iResolution.y, 1.0);


    float repeat = 5.0;
    float spiral = fract(
        // angle
        atan(uv.y,uv.x)*repeat/TAU
        // the further away from center, pretend the angle turned further
        + length(uv)
        // SPEEEEEEEEN
        - iTime
    );

    vec2 n_uv = vec2(length(uv), spiral);

    vec3 col = palette(sin(length(n_uv-.5)*4.0-iTime), 
        vec3(.5), vec3(.5), vec3(2.,1.,0.), vec3(.5,.2,.25)
    );	

    fragColor = vec4(mix(vec3(n_uv, 0.0), col, step(0.,uv.x)),1.0);
}
