#define PI 3.14159265359

float noise(vec2 uv) {
    return fract(sin(dot(uv.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

float bellShape(vec2 pos, vec2 center, float radius) {
    float dist = length(pos - center);
    float bellCurve = smoothstep(radius * 0.6, radius, dist) - smoothstep(radius, radius * 1.3, dist);
    return bellCurve;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 color = vec3(0.0);
    float bellEffect = 0.0;

    for (int i = 0; i < 5; i++) {
        vec2 center = vec2(
            noise(vec2(float(i) * 0.2 + iTime * 0.3, 0.5)) * 1.5,
            noise(vec2(float(i) * 0.2 - iTime * 0.1, 1.5)) * 1.2
        );

        float radius = 0.15 + noise(vec2(float(i) * 0.5, iTime)) * 0.2;

        center.x += sin(iTime * (0.8 + float(i) * 0.3)) * 0.05;
        center.y += cos(iTime * (1.0 + float(i) * 0.2)) * 0.05;

        bellEffect += bellShape(uv, center, radius) * (0.8 + noise(vec2(float(i) * 0.3, iTime * 0.5)));
    }

    color = mix(vec3(0.1, 0.1, 0.15), vec3(0.8, 0.7, 0.4), bellEffect);
    fragColor = vec4(color, 1.0);
}
