vec3 circle(vec2 uv,float radius,float blur,vec2 pos,vec3 colorr)
{
    vec3 circlecolor = vec3(colorr);
    float d=length(uv-pos);
    float c=smoothstep(radius,blur,d);
    vec3 col=(c)*circlecolor;
    return col;
}



void mainImage(out vec4 fragColor,in vec2 fragCoord)
{  vec2 uv=fragCoord/iResolution.xy;
    uv-=0.5;

   uv.x*=iResolution.x/iResolution.y;
   vec3 color1=circle(uv,0.9,0.4,vec2(0.,0.),vec3(0.267,0.933,0.733)+vec3(0.7*sin(iTime*0.5)));
   vec3 color2=circle(uv,0.8,0.02,vec2(0.,0.)+vec2(0.3*sin(iTime*3.),0.3*cos(iTime*2.)),vec3(0.078,0.118,0.400));
   vec3 color3=circle(uv,0.5,0.4,vec2(0.,0.)+vec2(0.3/iTime),vec3(1.000,0.180,0.616));
   vec3 color4=circle(uv,0.5,0.4,vec2(0.,0.)+vec2(-0.3/iTime),vec3(0.000,0.224,0.902));
   vec3 finalcolor=color1+color2+color3+color4;
   fragColor = vec4(finalcolor, 1.0);
}
