#define S(v) smoothstep(1.,-1., v )

void mainImage( out vec4 o, vec2 f )
{
    //f = 2.*f/iResolution.xy - 1.;
    f = (2.*f-iResolution.xy)/iResolution.y + vec2(1e-9); // fixes black lines

    vec2 d = cos(6.*f);
    f *= mat2( 1./f, d/f ); // holy moly

    //vec2 d = 2.*cos(f);
    //f *= mat2( d, f.y/f.x, -d.y );

    float T = iTime*.1,
        c = cos(T),
        s = sin(T);

    f *= mat2(c, s, -s, c);

    float W = T + dot(d, f),
        v = cos(W*3.)*1.5;

    o = ( .5+.5*cos(W + vec4(0,2,4,0)) )
        *(S( (abs(v)-.5)/fwidth(v) )
        + S( (v+.5)/4. ) 
        - S( abs(v+.5) )
        );
}
