#define X iResolution.x
#define Y iResolution.y

vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1);
    vec3 d = vec3(0,0.33,0.67);

    return a + b*cos( 6.2*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord / iResolution.xy);
    vec2 uv0 = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    uv = fract(uv * 4.0)- 0.5;
    vec3 final = vec3(0.0);
    vec3 col;

    int wy = int(Y)/4;
    int wx = int(X)/4;

    wx = int(fragCoord.x)/wx % 4;
    wy = int(fragCoord.y)/wy % 4 ;



    for (float i = 0.0; i < 2.0; i++) {
        uv = fract(uv * 1.6) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        d = cos(d*8. + iTime)/8.;
        d = abs(d);

        col = palette(float((wx + wy)) * iTime*.2 + i*.5 );

        d = pow(0.01 / d, 1.1);

        final += col * d;
    }


    fragColor = vec4(final, 1.0);
}
