void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = uv.y * 2.0 - 1.0;
    uv *= 2.0;

    float sine = sin(uv.x * 6.283185);
    float stair = round(sine);
    float square = sign(sin(uv.x * 6.283185));
    float triangle = abs(fract(uv.x - 0.25) * 2.0 - 1.0) * 2.0 - 1.0;
    float sawtooth = (fract(uv.x) * 2.0 - 1.0) * -1.0;
    float combo = (sine + stair + square + triangle + sawtooth) / 5.0;


    float t = fract(iTime / 10.0);

    float v = mix(sine, stair, clamp(t * 6.0, 0.0, 1.0));
    v = mix(v, square, clamp((t - 1.0/6.0) * 6.0, 0.0, 1.0));
    v = mix(v, triangle, clamp((t - 2.0/6.0) * 6.0, 0.0, 1.0));
    v = mix(v, sawtooth, clamp((t - 3.0/6.0) * 6.0, 0.0, 1.0));
    v = mix(v, combo, clamp((t - 4.0/6.0) * 6.0, 0.0, 1.0));
    v = mix(v, sine, clamp((t - 5.0/6.0) * 6.0, 0.0, 1.0));

    float c = abs(v - uv.y);//smoothstep(v, v, uv.y);

    fragColor = vec4(vec3(1.0-pow(c, 0.2)),1.0);
}
