highp float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 col = texture(iChannel0,vec2(vec2(fragCoord.x,fragCoord.y) / iResolution.xy)).rgb;  
    float x = ceil(fragCoord.x/(iResolution.x/128.0));// <- change these floats
    float y = floor(fragCoord.y/(iResolution.y/72.0));//<- to change the number of rectangles on the screen
    float pix=x+(iResolution.x*y)+iTime;
    //thanks elenzil
    for (int n = 0; n < 3; ++n) {
    col[n] = step(rand(vec2(pix, n)), col[n]);
}
    fragColor = vec4(col,1.0);

}
