// Author: HerpesOfBalkan
// Title: cool boring colors

float abc(float x) {
    return sin(iTime + x * 3.14 / 3.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    vec3 color = vec3((abc(0.0)),
                      (abc(2.0)),
                      (abc(4.0)));

    vec3 tmp = vec3(st.y * 0.6 + st.x * 0.2 * st.x + abs(tan(sin(iTime * 0.2)))) * 0.5;
    color += tmp;

    color = ceil(color * 4.0) / 4.0;

    color += vec3(sin(st.y * 3.0) * 0.2);

    fragColor = vec4(color,1.0);
}
