void mainImage( out vec4 f, in vec2 g )
{
    f -= f;
    float x = 1.0, i = x, T = iTime, l;
    vec2 R = iResolution.xy;
    vec4 p = vec4((g + g - R) / R.y, 0.3, 0), d = p;


    float angle = T * 0.3;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)); 
    p.xy = rot * p.xy; 

    float PSD = pow(textureLod(iChannel0, vec2(0.5), 0.0).r, 1.0); 


    for (; i > 0. && x > 1e-3; i -= 0.02) {

        x = length(sin(p.wxw * 3.0) - cos(p.yzw * 3.0)) - PSD * 0.3;


        vec3 color = mix(vec3(1.0, 0.2, 0.0), vec3(1.0, 0.5, 0.0), abs(sin(iTime * 0.3))); 


        float factor = sin(iTime * 5.0 + length(p.xy * 2.0));
        f += vec4(i * i * vec4(l = distance(p, p - p), 1. / l * l, abs(p.y), 1.)) * vec4(color, 1.0) * factor;

        p -= d * x * 0.4;
    }
}
