void mainImage( out vec4 Q, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = U/R.xy;
    float ratio=R.x/R.y;
    vec4 col;

    if (uv.y >0.5 && uv.x < 0.5/ratio)
    {
        vec2 uv2=uv;
        uv2.y=(uv2.y-0.5)*2.;
        uv2.x/=0.5/ratio;

        col += texture(iChannel0,uv2).rgba;
        col += abs(sdBox(uv2));

    }

    if (uv.y <0.5 && uv.x < 0.5/ratio)
    {
        vec2 uv2=uv;
        uv2.y*=2.;
        uv2.x/=0.5/ratio;

        col+= texture(iChannel1,uv2).rgba;
        col+=abs(sdBox(uv2));
    }

    if (uv.x>0.5/ratio)
    {
        vec2 uv2=uv;
        uv2.x-=0.5/ratio/2.;
        col+= texture(iChannel2,uv2);
    }


    // Output to screen
    Q = vec4(col);
}

// Square billard trajectory

void mainImage( out vec4 Q, in vec2 U )
{
    vec2 uv = U/R.xy;
    float ratio=R.x/R.y;
    vec2 mouse=M.xy/R.xy;
    vec4 A=texture(iChannel0,uv);
    vec4 D=texture(iChannel1,uv);
    float a=atan2(iFrame >0 ? D.xy :  vec2(0.1,0.28));//angle mouse
    float r=D.w;//reset
    float t=iTime-D.z*CT;//time stored
    float h=texture(iChannel2,uv).a; //highlight area

    if ( r==1.)
    {
        Q = vec4(0.);
        return;
    }

    vec2 p1=trajectory(P_START, a, speed*t-time_step);
    vec2 p2=trajectory(P_START, a, speed*t+time_step);

    float sdf_value =sdSegment(uv, p1, p2, R_seg);
    sdf_value=sdf_value<0.?1.:0.;

    if (sdf_value>0.)
    {
        if (h==1.)
            Q = A+col1;
        else
            Q = A+col2;
    }
    else
        Q=A;
}

#define R iResolution.xyz
#define M iMouse.xyz
#define rot(a)    mat2( cos(a+vec4(0,11,33,0)) )              // rotation                  
//11 : 3pi/2, 33 : pi/2, 23 : pi/3, 21 : 2pi/3

const float TAU=2.*3.1415926;
const vec2 P_START=vec2(0.5,0.5);

float R_seg=0.01;
float speed=.3;
float time_step=.005;
float R0 = 15., R1 = 7.;                                 // large and small radii
float bg=0.6;
float CT=1000.;//compress time

vec4 col1=vec4(1.,0.,0.,1.);//line trajectory color
vec4 col2=vec4(0.,1.,1.,1.);//highlights area color

float sdSegment( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}

float sdBox( in vec2 p)
{
    float r=0.495;
    vec2 b=vec2(0.5);
    vec2 d=abs(p-b);
    return step(r,max(d.x,d.y));
}

float sin_triangle(float t)
{
    return (-cos(t*TAU)+1.)/2.;
}
float triangle(float t)
{
    return 2.*abs(t-floor(t+0.5));
}

float unfold_triangle(float t)
{
    return 2.*abs(t-floor(t));
}

vec2 trajectory(in vec2 p_start, in float a_start, float t)
{
    // square trajectory billard solution
    // https://www.desmos.com/calculator/yrqgqml8hm
    // https://www.youtube.com/watch?v=nJWtSOxqyLo&t=480s
    return vec2(triangle(t*cos(a_start)+p_start.x+0.25), triangle(t*sin(a_start)+p_start.y+0.25));
}


vec2 flat_torus_trajectory(in vec2 p_start, in float a_start, float t)
{
    // square torus shape 
    // https://www.desmos.com/calculator/mce4n9pyxo
    vec2 res = vec2(unfold_triangle(t*cos(a_start)+p_start.x+0.25), unfold_triangle(t*sin(a_start)+p_start.y+0.25));
    return (2.-res).yx;
    //return vec2(2.-res.y,2.-res.x);
}

vec2 direction(float angle)
{
    return vec2(cos(angle),sin(angle));
}

float atan2(vec2 p)
{
    // atan for full angle [-pi,pi] and normalize to [0,2*pi]
    if (p==vec2(0.))
        return 0.;
    return 2.*atan(p.y/(length(p)+p.x))+TAU/2.;
}

float atan2(float px, float py)
{
    // overload
    return atan2(vec2(px, py));
}

// Unfolded Square trajectory

void mainImage( out vec4 Q, in vec2 U )
{
    vec2 uv = U/R.xy;
    float ratio=R.x/R.y;
    vec2 mouse=M.xy/R.xy;
    vec4 B=texture(iChannel0,uv);
    vec4 D=texture(iChannel1,uv);
    float a=atan2(D.xy);//angle mouse
    float r=D.w;//reset
    float t=iTime-D.z*CT;//time stored

    uv*=2.;


    if ( r==1.|| iFrame==0)
    {
        B=vec4(0.);
            if (uv.x<1.&&uv.y>1.) // bottom left area
                Q = B+col2*0.7;
            if (uv.x>1.&&uv.y<1.) // upper right area
                Q = B+col2*0.7;
        return;
    }

    vec2 p0=flat_torus_trajectory(P_START*2., TAU/4.-a, speed*t-2.*time_step);
    vec2 p1=flat_torus_trajectory(P_START*2., TAU/4.-a, speed*t-time_step);
    vec2 p2=flat_torus_trajectory(P_START*2., TAU/4.-a, speed*t+time_step);

    // draw segment only if point is close
    // otherwise it goes means it goes beyond cyclic boundaries
    // and should not be drawn
    if (distance(p1,p2)>0.1)
    {
        p2=p1+2.*(p1-p0);
    }
    else if (distance(p0,p1)>0.1)
    {
        p1=p1-2.*(p2-p1);
    }

    float sdf_value =sdSegment(uv, p1, p2, R_seg);
    sdf_value=sdf_value<0.?1.:0.;

    if (sdf_value>=0.)
    {
        if ((p1.x<1. && p1.y>1.) || (p1.x>1. && p1.y<1.))
        {
            Q = B+sdf_value*col2;
            Q.a=0.;
        }
        else
        {
            Q = B+sdf_value*col1;
            Q.a=1.;
        }   
    }
}

// Torus https://www.shadertoy.com/view/sdd3R4

void mainImage(out vec4 O, vec2 U) {    
    float t=9.,d,  k = .025; // .9                              // tile multiplier

    vec2 uv = U/R.xy;
    float ratio=R.x/R.y;
    vec3  D = normalize(vec3( U+U, -3.5*R.y ) - R ),          // ray direction
          p = 90./R, q,                                       // marching point along ray 
          nM = M.xyz/R -.5;

    for ( O=vec4(0) ; O.x < 1. && t > .2 ; O+=.025 )
    {
        q = p;
        q.yz *= rot(TAU/2.-4.*nM.y);                              // rotations
        q.xz *= rot(TAU/8.+6.*nM.x);
        t = min(100., length(vec2(d=length(q.xz)-R0,q.y)) - R1);// abs for inside + outside
        p += t*D;                                             // step forward = dist to obj        
        }
    O = O.x > 1. ? vec4(0.) : exp(-3.*O/2.);                  // luminance (depth + pseudo-shading )

    // Compute angle in [0,1] and non negative value for uv mapping
    float a = atan2(q.x,q.z)/TAU, b = atan2(q.y,d)/TAU;
    //b=mod(b+0.*iTime/10.,1.);
    //O+=vec4(0.25);
    O=O.x*texture(iChannel0,vec2(a,b).xy)+(1.-O.x)*(col2)/3.;//+vec4(0.25);
    //O+=0.1;
    }

void mainImage( out vec4 Q, in vec2 U )
{
    vec2 uv = U/R.xy;
    float ratio=R.x/R.y;
    vec2 mouse=M.xy/R.xy;
    vec4 tex=texture(iChannel0,uv);
    if (iFrame==0)
    {
        Q = vec4( vec2(0.1,0.28),0., 1.);
                return;
    }
    // upper left corner condition
    if (M.z>0. && mouse.y >0.5 && mouse.x < 0.5/ratio)
        {
            //rescale to upperleft in [0,1]Â²
            mouse.x=mouse.x*ratio*2.-0.5;
            mouse.y=(mouse.y-0.5)*2.-0.5;

            mouse=floor(mouse*60.)/60.;// give more closing/looping trajectory

            // reset condition
            if (atan2(tex.xy)!=atan2(mouse))
            {
                Q = vec4( mouse.x, mouse.y,iTime/CT, 1.);
                return;
            }
        }
    Q = vec4(tex.xyz,0.);//+vec4(0.1,0.,0.,0.);
}
