vec3 palette( float t ){
     vec3 a = vec3(cos(tan(iTime))*0.74 +0.5,cos(iTime)*0.61+0.5,sin(tan(iTime+0.5))*0.4637+0.5);
     vec3 b = vec3(.34,.91,.23);
     vec3 c = vec3(1.,1.,1.);
     vec3 d = vec3(0.263,0.416,0.557);
     return a+b*sin( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy *2.0-1.0;
    vec3 fcol=vec3(0.);
    uv.x *= iResolution.x/iResolution.y;

    vec2 uv0=uv;

    float f = length(uv0) * 3.;

    for (float i =0.0; i<f; i++){

        uv *=1.5;

        uv =fract(-uv);

        uv-=0.5;
        float x = length(uv) * exp(-length(uv0));

        x=cos(x*32.+iTime)/2.;

        x=abs(x+0.75);

        x=pow(0.1/x,1.2);

        vec3 col = palette(length(uv0)+iTime*0.4);

        float d = length(uv) * exp(-length(uv0));

        d=tan(d*32.+iTime)/2.;

        d=abs(d+0.8);

        d=pow(0.1/d,1.2);

        fcol+=col*d + col*x;
    }
    fragColor = vec4(fcol,1.0);
}
