void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 solid = vec4(0.9,0.45,0.2,1.0);
    vec2 d1 = xy - vec2(cos(atan(xy.y, xy.x)), cos(atan(xy.x, xy.y)));
    vec2 d2 = xy - vec2(cos(atan(xy.x, xy.y)), cos(atan(xy.x, xy.y)));
    vec2 d3 = xy - vec2(cos(atan(xy.y, xy.x)), cos(atan(xy.y, xy.x)));
    vec2 d4 = xy - vec2(sin(iTime), cos(iTime));
    vec2 d5 = xy - vec2(tan(iTime), atan(iTime));

    float h1 = 0.5*cos(iTime);
    float h2 = 0.4*cos(iTime);
    float h3 = 0.7*cos(iTime);
    float h4 = 0.9*cos(iTime);

    float r1 = 0.2 + h1*cos(atan(d1.x, d1.y)*iTime*10.0 + 40.0 * h1 + 40.0*iTime);
    float r2 = 0.45 + h2*cos(atan(d2.y, d2.x)*iTime*20.0 + 10.0 * h2 + 50.0*iTime);
    float r3 = 0.3 + h3*cos(atan(d3.x, d3.y)*iTime*60.0 + 50.0 * h3 + 20.0*iTime);
    float r4 = 0.6 + h4*cos(atan(d3.x, d3.y)*iTime*80.0 + 10.0 * h4/2.0 + 15.0*iTime);

    solid.r *= smoothstep(r1, r1 + 0.1, length(d1)) + smoothstep(r4, r4+0.2, length(d4));
    solid.g *= smoothstep(r2, r2 + 0.2, length(d2));
    solid.b *= smoothstep(r3, r3 + 0.6, length(d3)) - smoothstep(r4, r4+0.3, length(d5));
    fragColor = solid;
}
