vec3 tint = vec3(255, 255, 255);
// these are by rgb values to make tinting easier.
// you can modify the shader so the values are in decimal form.
// in fact, you can do whatever you want with this shader.
// it's public domain.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture(iChannel0, uv);
    float greyscale = (tex.r + tex.g + tex.b) / 3.0;
    fragColor = vec4(greyscale * (tint[0] / 255.0), greyscale * (tint[1] / 255.0), greyscale * (tint[2] / 255.0), tex.a);
}
