const ivec2 FS_ADDR = ivec2(0.0);

const vec4 fs_stt = vec4(1.0);

uint seed = 0u;

void setSeed(uint s) {
    seed = s;
}

uint hash(uint s) {
    s ^= 2747636419u;
    s *= 2654435769u;
    s ^= s >> 16;
    s *= 2654435769u;
    s ^= s >> 16;
    s *= 2654435769u;
    return s;
}

uint randI() {
    seed = hash(seed);
    return seed;
}

float rand() {
    uint rn = randI();
    rn %= 1000000000u;
    float rs = float(rn);
    rs /= 1000000000.0;
    return rs;
}

#define setNSeed() setSeed(uint(iResolution.x*fragCoord.y+fragCoord.x ))

// This just paints whatever is in BufferA

vec2 mat(vec2 v,vec2 shift,float a, float b){

//float n=0.05+0.04*cos(float(iFrame)/480.);
float n=0.25;
float l=cos(n*length(shift)+float(iFrame)/(30.*1.)+a);
float l2=cos(n*length(shift)+float(iFrame)/(30.*1.)+b);
return vec2(0.4*l*v.x+l2*0.2*v.y,-l2*0.2*v.x+0.4*l*v.y);
}


void mainImage(out vec4 color, in vec2 uv) {

    if(mod(float(iFrame),1.)==0.){
    vec2 fragCoord=uv;
    color=vec4(0.);
    float n=19.75;
    uv.x = n*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = n*(uv.y-0.5*iResolution.y)/iResolution.y;
    float f=3./1.;
    float g=30.;
    vec2 p=vec2(0.,0.);
    setNSeed();

    for(float i=1.;i<=100.;i+=1.){
    float r=ceil(5.*rand());

    vec4 c=vec4(0.);
    if(r==1.){
    p=mat(p,uv,0.,0.);
    c=vec4(0.8,0.5,0.1,1.);
    }
    if(r==2.){
    p=mat(p,uv,0.,0.)+vec2(0.,1.);
    c=vec4(0.5,0.8,0.1,1.);
    }
    if(r==3.){
    p=mat(p,uv,0.,0.)+vec2(-1.,0.);
    c=vec4(0.5,0.1,0.8,1.);
    }
    if(r==4.){
    p=mat(p,uv,0.,0.)+vec2(1.,0.);
    c=vec4(0.8,0.1,0.5,1.);
    }
    if(r==5.){
    p=mat(p,uv,0.,0.)+vec2(0.,-1.);
    c=vec4(0.1,0.8,0.5,1.);
    }

    for(float j=-4.5;j<=4.5;j+=0.5){

    for(float u=-7.;u<=7.;u+=1.){

    if(abs(uv.x-(p.x+4.*j+u))<0.25 && abs(uv.y-(p.y+2.*j-2.*u))<0.25){
    color += f*c*vec4(pow(2.,-g*length(uv-vec2(p.x+4.*j+u,p.y+2.*j-2.*u)))); 
    }
    }

    }
    }

    }else{
     color = texture(iChannel0, (uv)/iResolution.xy); 

    }





}
