vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

void mainImage(out vec4 o, vec2 u)
{
    float i,e,R,s, t = iTime;
    vec3 q,p,r = iResolution,d=vec3((r.xy-u.xy*2.)/r.x-vec2(2,2.5),2);
    o *= 0.;
    for(q.zy--;i++<40.;)
    {
        e+=i/9e2;
        o.rgb+=.3-hsv(.6,e,min(e*s,R)/8.);
        s=3.;
        p=q+=d*e*R*.04;
        p=vec3(log2(R=length(p))-t,exp(-p.z/R),atan(p.y,p.x));
        for(e=--p.y;s<1e3;s+=s)
            e+=abs(dot(cos(p*s),cos(p.zxy*s))/s*.7);
    }
}
