void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec2 uvOffset = uv.xy * 10.0;
    vec3 rgbColOffset = vec3(0.0,2.0,4.0);    
    vec3 timeOffset = vec3(0.2,0.5,1.0) * iTime;

    vec3 col = sin(uvOffset.y + rgbColOffset + sin(uvOffset.x - rgbColOffset - timeOffset));


    // Output to screen
    fragColor = vec4(col,1.0);
}
