// Conversion formula from ~the internet~
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// Conversion forumla--once again--from ~the internet~
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

int saturate = 0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // An oscillating value to showcase the flexability of this form of posterization
    float steps = mod(iTime, 5. * (3.1415826));

    saturate = int(abs(floor(sin(iTime / 5.))));

    // Normalizing so the texture won't be stretched
    vec2 uv = fragCoord/iResolution.xy;

    // Get the texture, and immediately set it to hsv color space, where it's easier to quantize without artifacts
    vec3 col = rgb2hsv(texture(iChannel0, uv).xyz);

    // A brightness-based posterization effect, makes everything feel shiny
    if (saturate == 0) {
        col.z = round(col.z * steps) / steps;
    } 

    // A saturation-based posterization effect, makes everything feel calm
    else {
        col.y = round(col.y * steps) / steps;
    }

    //convert the hsv values back to rgb for display
    col = hsv2rgb(col);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
//Hope this helps someone
