void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = (-1.0+2.0*q)*vec2(iResolution.x/iResolution.y,1.0);
    float d = length(p)*1.0+0.5;
    float phi = atan(p.y/p.x)+iTime*0.5;
    float dp = (2.0+0.2*sin(phi*20.0));
    d = d + dp;
    vec2 uv = vec2(p)/d;
    uv = uv + iMouse.xy/iResolution.xy;
    uv = uv -vec2(0.5,0.5);
    uv = uv*1.0*(2.0+sin(iTime*1.0));
    uv = uv +vec2(0.5,0.5);
    vec3 col = texture(iChannel0, uv).rgb;
    col = col*3.0/d;
    col = col-vec3(p.y);
    fragColor = vec4(col, 1.0);
}
