bool drawCircle(vec2 fragCoord, vec2 center, float radius, vec2 resolution) {
    vec2 centerNorm = vec2(center.x, resolution.y - center.y);
    vec2 normFragCoord = fragCoord - centerNorm;
    return dot(normFragCoord, normFragCoord) <= radius * radius;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = vec2(200.0, 200.0);
    float radius = 100.0;

    if (drawCircle(fragCoord, center, radius, iResolution.xy)) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
