//logic taken from gaz: https://www.shadertoy.com/view/ftKBzt
//and made more verbose and tweaked

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos( h + vec3(1,3,4) )*.6 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c*c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0), rd = normalize( vec3(U-.5*R.xy, R.y))*8.;

    float sc,dotp,totdist=0., t=iTime/4.;

    for (float i=0.; i<150.; i++) {

        vec4 p = vec4( rd*totdist, 0.);

        p.xyz = ROT(p.xyz+vec3(-1.5,0,-3), normalize( vec3(sin(t/5.),cos(t/3.),0)  ),t);
        sc = 1.;  //scale factor

        float dd=5.;


        for (float j=0.; j<7.; j++) {

            p = abs(p)*.67 ;

            dotp = max(1./dot(p,p),.1);
            sc *= dotp * 1.05;

            p = p.wzxy;                     //always reflect xy and wz

            p = abs( p ) * dotp - vec4(.1,.9,1.2,.5); 
        }

        float dist = abs( length(p.x*p.y*p.z)-.9)/sc ;  //funky distance estimate
        float stepsize = dist/(max(1.,1.+2.*sin(t*4.))) + 1e-4;     //distance plus a little extra
        totdist += stepsize;              //move the distance along rd


        //accumulate color, fading with distance and iteration count
        c += mix( vec3(1), H(M(sc)),.7) *.01*  exp(-i*i*stepsize*stepsize/20.);
    }

    c *= c;
    c = 1. - exp(-c);
    O = ( vec4(c,0) );

}
