Main {
    Q = vec4(0);
    vec4 QB = vec4(0);
    for (float x=-3.;x<=3.;x++)
    for (float y=-3.;y<=3.;y++)
    {
          vec2 xy = vec2(x,y);
          vec4 b = B(U+xy);
