const float PI = 3.141592653;

// 8< original https://thebookofshaders.com/08/ by @patriciogv
mat2 rotate2d(float _angle){
    float s = sin(_angle), c = cos(_angle);
    return mat2(c,-s,s,c);
}
// 8< end snip

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 ar = vec2(1.,iResolution.y/iResolution.x);
    vec2 uv = fragCoord;
    vec2 center = iResolution.xy * vec2(0.5);
    vec2 mouse = vec2(0.5, 0.2);
    mouse = (iMouse.z > 0.5) ? iMouse.xy/iResolution.xy : mouse;

    float scale = 0.581 * mouse.y+0.01;
    float speed = 0.05;
    float time = iTime;

    uv -= center;
    uv = rotate2d( time * PI * speed )*uv;
    uv += center;

    float r = 0.5 + sin( (uv.x + cos(uv.y / 20.) * 14.    )*scale / PI) * 0.5;

    uv -= center;
    uv = rotate2d( time * PI * speed )*uv;
    uv += center;

    float g = 0.5 + sin( (uv.x + cos(uv.y / 20.) * 14.    )*scale / PI) * 0.5;

    uv -= center;
    uv = rotate2d( time * PI * speed )*uv;
    uv += center;

     float b = 0.5 + sin( (uv.x + cos(uv.y / 20.) * 14.    )*scale / PI) * 0.5;

    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}
