void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    //Normalized screen space Coordinates
    vec2 uv = fragCoord / iResolution.xy;

    //translating the coordinates to the center
    uv -= 0.5;

    //rotate the coordinates based on rotation matrix
    float Angle = iTime;

    //2D rotation matrix to rotate CCW
    mat2 rotMat = mat2(cos(Angle), -sin(Angle), sin(Angle), cos(Angle));
    uv = rotMat * uv;

    fragColor = vec4(uv, 0.7f, 1.0f);
}
