// Construction of the radius formula:
// The leafs of the flower-shape are circle-arcs
// inside the unit-circle originating at (0,-1).
// Each circle-arc is tangential to the y axis.
// wxmaxima:
// circle:(x-x_0)^2+(y-y_0)^2=r^2; /* the leafs of the flower are circles */
// origin:(0-x_0)^2+(-1-y_0)^2=r^2; /* the origin is at (0,-1) */
// tangent:x_0=r; /* circles are tangent to y axis (orthogonal to unit circle) */
// solve([circle, origin, tangent],[x_0,y_0,r]);
// [[x_0=(y^2+2*y+x^2+1)/(2*x),y_0=-1,r=(y^2+2*y+x^2+1)/(2*x)]]
float ortho_circle_flower_sdf(vec2 p) // https://www.shadertoy.com/view/clGBzm orthogonal circles flower sdf 2, 2023 jt
{
    float n = 11.0; // must be odd integer

    float slice = pi/n;

    p.x = abs(p.x); // mirror horizontally
    float r = (p.y*p.y+2.0*p.y+p.x*p.x+1.0)/(2.0*p.x); // calculate radius of circular arc leaf passing through this point

    // equidistant circle segment endpoints on unit circle
    float r0 = tan(floor(atan(r)/slice)*slice); // to angle then quantize then to length
    float r1 = tan( ceil(atan(r)/slice)*slice); // to angle then quantize then to length

    // circle center
    vec2 c0 = vec2(r0,-1); // lower leaf
    vec2 c1 = vec2(r1,-1); // upper leaf

    // endpoints
    float mu = atan(p.x,p.y);
    mu = (floor(mu/slice/2.0)+0.5)*slice*2.0;
    vec2 c = vec2(sin(mu),cos(mu));

    return
        //length(p) < 1.0; // approximate
        dot(p, c) < 1.0 // thanks to tdhooper for fixing remaining discontinuities by changing length(p) < 1.0 to dot(p, c) < 1.0 (turns circle into polygon)
        ?
        min(abs(length(p-c0)-r0),abs(length(p-c1)-r1)) // circular arc leafs
        :
        length(p-c); // leaf endpoints
}

float smin(float a, float b,float k )
{
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0) ;
    return mix(b, a, h ) - k * h * (1.0 - h);
}

float smax(float a, float b, float k)
{
    return -smin(-a,-b, k);
}

float TLC123_tree2d(vec2 uv, int branches) // https://www.shadertoy.com/view/sl2SRt 2d tree branching SDF by TLC
{
    // to polar
    vec2 p = vec2((atan(uv.x, uv.y) / tau) * float(branches), length(uv));

    float Y, y, X;
    Y = exp2(floor(p.y));
    y = 0.5 + (pow(fract(p.y), 0.85)) * 0.5 + 0.5;
    X = abs(p.x * Y);
    p.x = abs(fract(X - y * .5) - 0.5) * 2.0;
    p.x = max(p.x, abs(fract(X + y * 0.5) - 0.5) * 2.0);

    float branchwidth = 0.1 / (0.25 + p.y);
    float outerlimit = p.y - 4.6;
    float scaleAdjusted = 1.5 * (((1.0 - p.x) / Y) * p.y);
    return smax(outerlimit, scaleAdjusted - branchwidth, 0.2) * 0.5;
}

struct result
{
    vec3 normal;
    float dist;
    vec3 color;
};

result combine(result a, result b)
{
    //return a.dist < b.dist ? a : b; // ??? why doesn't this compile ???
    if(a.dist < b.dist)
        return a;
    return b;
}

