// Simple Sine Waveform Shader

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the resolution and coordinates
    vec2 uv = fragCoord / iResolution.xy;

    vec3 rgb = 0.5 + .5 * cos(iTime*.5 + uv.xyx + vec3(7,2,6));

    // Calculate the sine wave
    float wave = sin(uv.x * sin(iTime/8.) + iTime);

    // Set the color based on the sine wave
    vec3 color = mix(vec3(1.), rgb, abs(uv.y - .5 - wave));

    // Output the color
    fragColor = vec4(color, 1.0);
}
