//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.1, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(1.263,0.416,0.557);

    return a*sin( 15.28318*(c*t+d)) + b*cos( 6.28318*(c*t+d) );
}

//https://www.shadertoy.com/view/mtyGWy
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);
    ;
    float a2;
              for(float z=0.; z<1.; z+=2./4.) {
    for(float y=0.; y<1.; y+=2./4.) {
    for(float x=0.; x<1.; x+=2./4.) {
  a2=texture(iChannel0, vec2(x*y*z*1.1,.0)).r;
 }}}
  uv+=cos(iTime)+a2;
   uv+=sin(iTime)+a2;
    for (float i = 0.0; i < 4.0+a2; i++) {

        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*1.4*a2 + iTime*0.4);

        d = sin(d*2. + iTime)/8.;
        d = abs(d)*a2;

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
