// Custom uniforms with default values
#define uNumSticks 24.0
#define uRotationSpeed 0.5
#define uStickWidth 0.05
#define uTwistAmount 2.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float radius = length(uv);
    float angle = atan(uv.y, uv.x);

    // Apply twisting effect
    angle += radius * uTwistAmount + iTime * uRotationSpeed;

    // Calculate stick index
    float stickIndex = mod(angle * uNumSticks / (2.0 * 3.14159265), 1.0);

    // Create stripes
    float stripe = step(0.5, stickIndex);

    // Create the tunnel effect by fading the stripes with distance
    float tunnel = exp(-radius * 2.0);

    vec3 color = vec3(stripe * tunnel);

    fragColor = vec4(color, 1.0);
}
