// Fork of "Mini Mandelbrot 24 Lines" by spenceryonce. https://shadertoy.com/view/X3GGWV
// 2024-06-12 16:41:21
precision highp float;
#define MAX 150.0
#define r iResolution
void mandelbrot(vec2 uv, vec2 z, out vec3 col) {
    float i = 0.0;
    for(; i < MAX; i++) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + uv;
        if (length(z) > 2.0) {
            col = vec3(0.0);
            break;}}
    col = vec3(i / MAX);
}
void mainImage(out vec4 o, in vec2 fc) {
    vec2 uv = ((2.0 * fc.xy - r.xy) / r.y)+vec2(-0.5,0.);
    vec3 col = vec3(0.);
    mandelbrot(uv, vec2(0.), col);
    o = vec4(pow(col,vec3(0.4545)), 1.0);
}
