    p.x *= iResolution.x / iResolution.y;
    float t = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(p.xy) + .07)) * 2.2;
float si = sin(t);
float co = cos(t);
mat2 ma = mat2(co, si, -si, co);

    vec3 col = vec3(0.2, 0.3, 0.4);
    vec3 c = vec3(0.0);
    vec3 c2 = vec3(0.0);
     vec3 c3 = vec3(0.0);
    c += col / (abs(tan(hash(p.x) + cos(iTime + p.y+p.x) )));
    c2 += col / (abs(tan(hash(p.y) + cos(iTime + p.x +p.y )) ));
      c3 += col / (abs(tan(hash(p.x) + cos(iTime - p.y+p.x))));
    c += col / (abs(tan(hash(p.y) + cos(iTime - p.x+p.y) )));
