#define PI 3.14159265358979323846264338327950288

float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

float sdRing(vec2 p, float r, float w)
{
    return sign(sdCircle(p, r) * sdCircle(p, r + w));
}

float sdLine(vec2 p, float a, float w)
{
    float y = tan(a) * p.x;
    return abs((p.y - y) * cos(a)) - w / 2.0;
}

float sdBall(vec2 p, float a, float r, float R, float s)
{
    return sdCircle(vec2(p.x + r * cos(a) * sin(s * iTime + a), p.y + r * sin(a) * sin(s * iTime + a)), R);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    float values1[11] = float[11](max(sdCircle(uv, 0.8), sdLine(uv, 0.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 2.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 3.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 4.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 5.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 6.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 7.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 8.0*PI/10.0, 0.01)), max(sdCircle(uv, 0.8), sdLine(uv, 9.0*PI/10.0, 0.01)), sdRing(uv, 0.8, 0.01));
    float sdFigure1 = values1[0];
    for(int i = 1; i < 11; i++) {
        if(values1[i] < sdFigure1) {
            sdFigure1 = values1[i];
        }
    }
    float values2[10] = float[10](sdBall(uv, 0.0, 0.8, 0.04, 2.0), sdBall(uv, PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 2.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 3.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 4.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 5.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 6.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 7.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 8.0*PI/10.0, 0.8, 0.04, 2.0), sdBall(uv, 9.0*PI/10.0, 0.8, 0.04, 2.0));
    float sdFigure2 = values2[0];
    for(int i = 1; i < 10; i++) {
        if(values2[i] < sdFigure2) {
            sdFigure2 = values2[i];
