    }
    vec3 col = vec3((1.0 - step(0.0, sdFigure1)) * (clamp(mod(iTime, 10.0) - 4.0, 0.0, 1.0) - clamp(mod(iTime, 10.0) - 9.0, 0.0, 1.0)) + (1.0 - step(0.0, sdFigure2)));
    fragColor = vec4(col, 1.0);
}
