#define PI     3.14159265

float sphereIntersect(vec3 orig, vec3 dir, vec4 sphere)
{
    vec3 centre = orig-sphere.xyz;
    float b= dot(centre, dir);
    float c = dot(centre, centre)-sphere.w*sphere.w;
    float h = b*b-c;
    if(h<0.0) return -1.0;
    return -b-sqrt(h);
}

vec3 sphereNormal(vec3 hit, vec4 sphere)
{
return normalize((sphere.xyz-hit)/sphere.w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fov = 80.0;
    float size = tan(fov*PI/360.0);
    vec2 pndc = (fragCoord+0.5) / iResolution.xy;
    vec2 ps = vec2((2.0*pndc.x-1.0)*iResolution.x/iResolution.y,1.0-2.0*pndc.y)*size;
    vec3 ro = vec3(0.0, 0.0, 0.0 );
	vec3 rd = normalize( vec3(ps,3.0) );
    vec4 sphere  = vec4(6.*(iMouse.x/iResolution.x-0.5),0,4.*(iMouse.y/iResolution.y-0.5)+7.,1);
    fragColor=texture(iChannel0,pndc);
    float t = sphereIntersect(ro,rd,sphere);

    if(t>0.)
    {
        if(sphere.z>7.)
        {
            if(texture(iChannel0,pndc).y>0.3 && fragColor.x<0.2)
            {
               vec3 hit = ro+t*rd;
               vec3 n = sphereNormal(hit, sphere);
               vec3 l = normalize(vec3(iMouse.x,iMouse.y,1));
               fragColor=vec4(cos(iTime)*cos(iTime),sin(iTime)*sin(iTime),0,0.)*dot(n,l)+vec4(1,1,1,1)*pow(dot(n,l),5.);
            }
        }
        else
        {
               vec3 hit = ro+t*rd;
               vec3 n = sphereNormal(hit, sphere);
               vec3 l = normalize(vec3(-iMouse.x/iResolution.x,iMouse.y/iResolution.y,1));
               fragColor=vec4(cos(iTime)*cos(iTime),sin(iTime)*sin(iTime),0,0.)*dot(n,l)+vec4(1,1,1,1)*pow(dot(n,l),5.);
        }
    }

}
