vec2 rect2pol(vec2 uv) {
    float theta = atan(uv.x, uv.y);
    float r = sqrt(dot(uv,uv));

    return vec2(r, theta);
}

vec2 rect2hyp1(vec2 uv, float t)
{
    float c1 = cos(atan(uv.x, uv.y));
    return vec2(sqrt((abs(uv.x)+t)*(abs(uv.y) + t)), c1*c1);
}

float pat1(vec2 uv)
{
    vec2 rt = rect2hyp1(uv, 0.01f);

    return smoothstep(0.0f, 0.1f, sin(rt.s*320.0f + (rt.s)*50.0*sin(4.0*rt.t)));
}

vec4 gray(float x)
{
    return vec4(x,x,x,1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float stime = 1.0f * iTime;
    vec2 srot = vec2(sin(stime), cos(stime));
    vec2 uv1 = fragCoord/iResolution.xy - 0.5;
    vec2 uv2 = uv1 - iMouse.xy/iResolution.xy + 0.5;
    vec2 uv3 = reflect(uv2, srot);

    // Output to screen
    fragColor = gray(pat1(uv1)* pat1(uv2 + srot*0.1));
}
