#define R(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,-1,0);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*4.;
    p.z-=4.;
    p=R(iTime*.5);
    p=.1/abs(abs(p*fract(iTime*.3))-.01)-.02,
    d=max(max(p.x,p.z),p.y),
    d-=dot(p,p)/2e4;
e+=d;

}o=vec4(exp(p),1)*12./i;
}
