void mainImage( out vec4 rgba, in vec2 xy )
{
    // Change this number to 1 to see the correct output
    #define kNum 2

    float a = 0.;
    for(int u = 0; u < kNum; ++u)
    {
        for(int v = 0; v < kNum; ++v)
        {
            a = (xy.x + xy.y) / 100.;
        }
    }  

    rgba.xyz = vec3(1.) * step(0.5, fract(a));
    rgba.w = 1.;
}
