void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
     vec4 draw = texture(iChannel0,uv);
      fragColor = vec4(draw.rgb,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;
     vec4 prev = texture(iChannel0,uv);
      float x = uv.x - m.x;
      float y = uv.y - m.y;      
      float ax = 10.*sqrt(abs(x));
      float ay = 10.*sqrt(abs(y));      
       float r = max(ax, ay) - 4.;
        vec4 curr = vec4(r,0.0,1.0,  1.0);        
     float  q=1./128.;
     float  _q= 1. - q;

         fragColor = q*curr + _q*prev;
        //fragColor = max(curr,prev);
}
