int iter = 512;
vec2 mult(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}
vec2 div(vec2 a, vec2 b) {
    return vec2(a.x * b.x + a.y * b.y, a.y * b.x - a.x * b.y) / (b.x * b.x + b.y * b.y);
}
vec4 mandel(vec2 a) {
    vec2 f_c = a;
    vec4 add = vec4(6.0/float(iter),0.0,0.0,0.0);
    vec4 ret = vec4(0.0,0.0,0.0,1.0);
    for(int i = 0; i < iter && length(f_c) <= 2.0; i++) {
        f_c = mult(f_c,f_c) + a;
        ret+=add;
    }
    if(length(f_c) <= 2.0) 
        ret = vec4(1.0);
    return ret;
}
