// Fork of "Spiraling Hexes" by PGRacer. https://shadertoy.com/view/XfjXWW
// 2024-01-27 06:48:56



const vec2 s = vec2(1, 1.7320508);


vec4 calcHexInfo(vec2 uv)
{
    vec4 hexCenter = round(vec4(uv, uv - vec2(.5, 1.)) / s.xyxy);
    vec4 offset = vec4(uv - hexCenter.xy * s, uv - (hexCenter.zw + .5) * s);
    return dot(offset.xy, offset.xy) < dot(offset.zw, offset.zw) ? vec4(offset.xy, hexCenter.xy) : vec4(offset.zw, hexCenter.zw);
}

float calcHexDistance(vec2 p)
{
    p = abs(p);
    return max(dot(p, s * .5), p.x);
}



vec3 hsb2rgb( in vec3 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return (c.z * mix( vec3(1.0), rgb, c.y));
}



vec2 radialize(vec2 uv)
{
    uv *= vec2(2.0 * iResolution.x/iResolution.y, 2.0);
    uv -= vec2(iResolution.x/iResolution.y, 1.0);

    float t = atan(uv.y, -uv.x);

    t /= 6.283185;

    float l = length(uv);

    return vec2(t, l);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;


    vec2 uv = fragCoord / iResolution.xy;
    uv = radialize(uv);
    uv = vec2(uv.x, uv.y + uv.x * 1.9055);
    uv = calcHexInfo(uv * 10.0 + iTime).xy;
    float t = calcHexDistance(uv);
    float c = smoothstep(0.4, 0.55, t);
    fragColor.rgb = hsb2rgb(vec3(uv.x*mouse.x+uv.y*mouse.y+t*mouse.x/mouse.y*.345+c*.12321,0.9,1.0));
}
