#define h11(a) fract(sin(a * 453.2734) * 994.3434)
#define h21(p) fract(sin(dot(p, vec2(453.2734, 2734.453))) * 994.3434)

float col;

float rain(vec2 p) {
    float h, c, t;
    c = floor(p.x);
    t = iTime + h11(c) * 14.;
    h = 130.;
    p.y -= cos((c + t) / 10.) * 6. - fract(t) + 1.;
    p.y < .0 ? h = 1. : h;
    p.y +=(h / 2. - .5) * fract(t);
    col = h21(floor(p)); // wrong
    p.x = fract(p.x) - .5;
    p.y = mod(p.y, h) - h / 2.;
    return length(p) - .5;
}

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
    u = 25. * vec2(u + u - r) / r.y;
    o = smoothstep(.04, .0, rain(u) + .06) * vec4(.9, .8, 0, 0); // * col; 
}
