#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage(out vec4 o, in vec2 FC)
{
float i,j,k,d=.1,e,t=iTime; vec3 p,q=vec3(-2,0,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=2.;
    p=R3D(t*.3);

    for(j=0.;j++<4.;)
        p=(abs(p-.1)-.04),
        d=max(max(p.y,p.z),p.x*sin(t)),
        d-=dot(p,q)/1e4;

    e+=d;

}o+=vec4(exp(1./p*q),1)*3./i;
}
