void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0, uv);

    float CHROMATIC = uv.x<0.5? 0.0: 2.0;

    col.rgb += CHROMATIC * dFdx(col.rgb)*vec3(-1,0,1);

    fragColor = col;
    fragColor.rgb *= min(abs(fragCoord.x-0.5*iResolution.x), 1.0);
}
